import React from "react";
import { Form, Icon, Input, Button, Checkbox } from "antd";
import styled from "styled-components";
import SignUpForm from "./SignUpForm";

const MainLayout = styled.div`
  height: 100%;
  padding: 5%;
  h1 {
    text-align: center;
  }
`;

class SignInForm extends React.Component {
  state = {
    showSignUpForm: this.props.visible
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log("Received values of form: ", values);
      }
    });
  };

  handleSignUpForm = () => {
    this.setState({
      showSignUpForm: true
    });
  };

  renderForm() {
    const { getFieldDecorator } = this.props.form;
    if (!this.state.showSignUpForm) {
      return (
        <div>
          <h1>Sign In</h1>
          <Form onSubmit={this.handleSubmit} className="login-form">
            <Form.Item>
              {getFieldDecorator("username", {
                rules: [
                  { required: true, message: "Please input your username!" }
                ]
              })(
                <Input
                  size="large"
                  prefix={
                    <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />
                  }
                  placeholder="Username"
                />
              )}
            </Form.Item>
            <Form.Item>
              {getFieldDecorator("password", {
                rules: [
                  { required: true, message: "Please input your Password!" }
                ]
              })(
                <Input
                  size="large"
                  prefix={
                    <Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />
                  }
                  type="password"
                  placeholder="Password"
                />
              )}
            </Form.Item>
            <Form.Item style={{ display: "flex", flexDirection: "column" }}>
              <div style={{ display: "flex", justifyContent: "space-between" }}>
                {getFieldDecorator("remember", {
                  valuePropName: "checked",
                  initialValue: true
                })(<Checkbox>Remember me</Checkbox>)}
                <a className="login-form-forgot" href="">
                  Forgot password
                </a>
              </div>
              <div>
                <Button
                  size="large"
                  type="primary"
                  htmlType="submit"
                  className="login-form-button"
                  block
                >
                  Log in
                </Button>
              </div>
              <div style={{ marginTop: "2%" }}></div>
            </Form.Item>
          </Form>
          <Button
            size="large"
            type="button"
            className="login-form-button"
            block
            onClick={this.handleSignUpForm}
          >
            Dont have an account yet? Register Now!
          </Button>
        </div>
      );
    } else {
      return <SignUpForm />;
    }
  }

  render() {
    return <MainLayout>{this.renderForm()}</MainLayout>;
  }
}

const WrappedNormalLoginForm = Form.create({ name: "sign_in" })(SignInForm);

export default WrappedNormalLoginForm;
