import React from "react";
import styled from "styled-components";
import { Select, Icon, Button } from "antd";

const MainLayout = styled.div`
  height: 98vh;
  padding: 7%;
  margin-top: 2%;
  display: flex;
  flex-direction: column;
  align-content: space-between;

  .cartContainer {
    max-height: 520px;
    overflow: auto;
    .cartItem {
      display: flex;
      margin-top: 3%;
    }
  }
  .totalContainer {
    margin-top: 15%;
    .subtotal {
      border-color: #949494;
      border-style: solid;
      border-width: 0.5px;
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 3%;
      height: 60px;
      padding: 5%;

      span {
        font-size: 20px;
        font-weight: bold;
      }
    }
  }

  #imageRow {
    flex: 1;
    img {
      width: 90px;
    }
  }
  #detailRow {
    height: 100%;
    flex: 3;
    display: flex;
    flex-direction: column;
    align-content: space-around;
    margin-top: 5%;
    padding-left: 2%;
    .trash {
      font-size: 18px;
      margin-top: 5%;
    }
  }
  #priceRow {
    flex: 1;
    display: flex;
    align-items: center;
    span {
      font-size: 20px;
      font-weight: bold;
    }
  }
`;

class Cart extends React.Component {
  state = {};

  handleChange = value => {
    console.log(`selected ${value}`);
