                  <Option value="8">8</Option>
                </Select>
              </div>
              <div>
                <Icon type="delete" className="trash" />
              </div>
            </div>
            <div id="priceRow">
              <span>₱7500</span>
            </div>
          </div>
        </div>
        <div className="totalContainer">
          <div className="subtotal">
            <div className="left">Subtotal :</div>
            <div className="right">
              <span>₱12900</span>
            </div>
          </div>
          <div className="checkout">
            <Button
              style={{
                backgroundColor: "green",
                borderColor: "green",
                borderRadius: "0px"
              }}
              type="primary"
              block
              size="large"
            >
              Review and Check Out
            </Button>
          </div>
        </div>
      </MainLayout>
    );
  }
}

export default Cart;
